import org.apache.logging.log4j.Level;

def logLevel = logEvent.getLevel();
if (logLevel != Level.TRACE && logLevel != Level.DEBUG) {
  return true;
}

if (logEvent.getLoggerName() == null) {
  return false;
}

def parts = logEvent.getLoggerName().split("\\.");
def name = parts[parts.length - 1];

if ("PartitionBasedLoadBalancer".equals(name)) {
  return false;
}

if ("PartitionPumpManager".equals(name)) {
  def message = logEvent.getMessage();
  return message != null && message.getFormat().startsWith("On next");
}

return true;

import org.apache.logging.log4j.Level;

def logLevel = logEvent.getLevel();
if (logLevel != Level.TRACE && logLevel != Level.DEBUG) {
  return true;
}

if (logEvent.getLoggerName() == null) {
  return false;
}

def parts = logEvent.getLoggerName().split("\\.");
def name = parts[parts.length - 1];

if (!"ReactorSender".equals(name)) {
  return true;
}

if (logEvent.getMessage() == null) {
  return false;
} else {
  def format = logEvent.getMessage().getFormat();
  return !format.contains("On next") && !format.contains("process delivered message");
}

import org.apache.logging.log4j.Level;

def logLevel = logEvent.getLevel();
if (logLevel != Level.TRACE && logLevel != Level.DEBUG) {
  return true;
}

if (logEvent.getLoggerName() == null) {
  return false;
}

def parts = logEvent.getLoggerName().split("\\.");
def name = parts[parts.length - 1];

if (!"ReceiveLinkHandler".equals(name)) {
  return true;
} else {
  def message = logEvent.getMessage();
  return message != null && !message.getFormat().startsWith("onDelivery");
}
